Scripts.OnClick:
db.AutoRepair=self:GetChecked()
if(db.AutoRepair) then
  sml_inform("Auto repair now turned on");
else
  sml_inform("Auto repair now turned off");
end

db.GuildFundedRepair=self:GetChecked();
if(db.GuildFundedRepair) then
sml_inform("Guild funded repair now turned on");
else
sml_inform("Guild funded repair now turned off");
end

db.AutoBuy=self:GetChecked();
if(db.AutoBuy) then
sml_inform("Auto Buy now turned on");
else
sml_inform("Auto Buy now turned off");
end

Scripts.OnEnter:
local parent = self:GetParent();
local myscript = parent:GetScript("OnEnter");
if(myscript ~= nil) then
	myscript(parent);
end

Scripts.OnLeave:
local parent = self:GetParent();
local myscript = parent:GetScript("OnLeave");
if(myscript ~= nil) then
	myscript(parent);
end

